import incomeItem from "@/components/IncomeItem";
export default {
  name: "IncomeRank",
  data: function data() {
    return {
      num: 10,
      book: [],
      categories: []
    };
  },
  components: {
    'income-item': incomeItem
  },
  created: function created() {
    var _this = this;

    //先查询分类id和名称
    this.request.get("/api/category").then(function (res) {
      if (res.code === '200') {
        _this.categories = res.data;
      } //获取排行数据


      _this.request.get("/api/book/rank/", {
        params: {
          num: _this.num
        }
      }).then(function (res) {
        if (res.code === '200') {
          _this.book = res.data;
        }
      });
    });
  }
};
