import * as echarts from "echarts";
export default {
  name: "IncomeChart",
  data: function data() {
    return {
      sumIncome: 0,
      categoryIncomes: [],
      categoryNames: [],
      incomes: [],
      activeName: "bar",
      totalAll: 0,
      totalWeek: 0,
      totalMonth: 0,
      total: 0
    };
  },
  methods: {
    handleClick: function handleClick(tab) {
      switch (tab.name) {
        case "bar":
          this.total = this.totalAll;
          break;

        case "pie":
          this.total = this.totalAll;
          break;

        case "line1":
          this.total = this.totalWeek;
          break;

        case "line2":
          this.total = this.totalMonth;
          break;
      }
    }
  },
  mounted: function mounted() {
    var _this = this;

    var barChart = echarts.init(document.getElementById("bar"));
    var pieChart = echarts.init(document.getElementById("pie"));
    var lineChart1 = echarts.init(document.getElementById("weekLine"));
    var lineChart2 = echarts.init(document.getElementById("monthLine"));
    var barOption = {
      tooltip: {
        trigger: "item"
      },
      title: {
        text: "收入统计柱状图",
        x: "center"
      },
      label: {
        show: true,
        //是否显示
        position: "top"
      },
      xAxis: {
        type: "category",
        data: []
      },
      yAxis: {
        type: "value"
      },
      series: [{
        data: [],
        type: "bar"
      }]
    };
    var pieOption = {
      tooltip: {
        trigger: "item"
      },
      title: {
        text: "收入统计饼图",
        x: "center"
      },
      series: [{
        type: "pie",
        data: []
      }]
    };
    var lineOption1 = {
      tooltip: {
        trigger: "item"
      },
      label: {
        show: true //是否显示

      },
      title: {
        text: "本周收入",
        x: "center"
      },
      xAxis: {
        type: "category",
        data: ["周一", "周二", "周三", "周四", "周五", "周六", "周日"]
      },
      yAxis: {
        type: "value"
      },
      series: [{
        data: [],
        type: "line"
      }]
    };
    var lineOption2 = {
      tooltip: {
        trigger: "item"
      },
      label: {
        show: true //是否显示

      },
      title: {
        text: "本月收入",
        x: "center"
      },
      xAxis: {
        type: "category",
        data: []
      },
      yAxis: {
        type: "value"
      },
      series: [{
        data: [],
        type: "line"
      }]
    }; //渲染柱状图和饼图

    this.request.get("/api/income/chart").then(function (res) {
      if (res.code === "200") {
        var categoryIncomes = res.data.categoryIncomes;
        var categoryNames = categoryIncomes.map(function (item) {
          return item.categoryName;
        });
        var incomes = categoryIncomes.map(function (item) {
          return item.categoryIncome;
        });
        barOption.xAxis.data = categoryNames;
        barOption.series[0].data = incomes;
        barChart.setOption(barOption);

        for (var i = 0; i < categoryNames.length; i++) {
          var item = {
            value: incomes[i],
            name: categoryNames[i]
          };
          pieOption.series[0].data.push(item);
        }

        pieChart.setOption(pieOption); //计算总和

        var sum = 0;
        incomes.forEach(function (item) {
          sum += item;
        });
        _this.total = sum;
        _this.totalAll = sum;
      }
    }); //渲染本周折线图

    this.request.get("/api/income/week").then(function (res) {
      if (res.code === "200") {
        // lineOption1.xAxis.data = res.data.weekDays;
        var weekIncome = res.data.weekIncome;
        lineOption1.series[0].data = weekIncome;
        lineChart1.setOption(lineOption1); //计算本周总营收

        var sum = 0;
        weekIncome.forEach(function (item) {
          sum += item;
        });
        _this.totalWeek = sum;
      }
    }); //渲染本月折线图

    this.request.get("/api/income/month").then(function (res) {
      if (res.code === "200") {
        lineOption2.xAxis.data = res.data.monthDays;
        var monthIncome = res.data.monthIncome;
        lineOption2.series[0].data = monthIncome;
        lineChart2.setOption(lineOption2); //计算本月总营收

        var sum = 0;
        monthIncome.forEach(function (item) {
          sum += item;
        });
        _this.totalMonth = sum;
      }
    });
  },
  filters: {
    numFilter: function numFilter(value) {
      // 截取当前数据到小数点后两位
      var realVal = Number(value).toFixed(2); // num.toFixed(2)获取的是字符串

      return Number(realVal);
    }
  }
};
