import API from "@/utils/request";
var url = "/api/good";
export default {
  name: "GoodInfo",
  data: function data() {
    return {
      baseApi: this.$store.state.baseApi,
      good: {
        discount: "1"
      },
      standards: [],
      index: 0,
      standard: {},
      fileList: [],
      dialogFormVisible: false,
      categoryItems: [],
      checkedCategory: {}
    };
  },
  created: function created() {
    var _this = this;

    this.request.get("/api/category").then(function (res) {
      if (res.code === "200") {
        _this.categoryItems = res.data;
      }

      if (_this.$route.query.good) {
        _this.good = JSON.parse(_this.$route.query.good);
        _this.fileList = [{
          name: "原始图片",
          url: _this.good.imgs
        }];
        _this.checkedCategory = _this.categoryItems[_this.good.categoryId]; //从服务器获取商品规格信息

        _this.request.get(url + "/standard/" + _this.good.id).then(function (res) {
          if (res.code === "200") {
            var standards = JSON.parse(res.data);
            _this.standards = standards;
          } else {//没有规格
          }
        });
      }
    });
  },
  methods: {
    /*
    规格操作
     */
    editStandard: function editStandard(index) {
      this.standard = this.standards[index];
      this.index = index;
      this.dialogFormVisible = true;
    },
    addStandard: function addStandard() {
      this.standard = {};
      this.index = this.standards.length;
      this.dialogFormVisible = true;
    },
    saveStandard: function saveStandard() {
      if (this.standard.value == undefined || this.standard.value == "") {
        this.$message({
          type: "error",
          message: "请输入规格名称",
          showClose: true
        });
        return;
      }

      if (this.standard.price == undefined || this.standard.price == "") {
        this.$message({
          type: "error",
          message: "请输入价格",
          showClose: true
        });
        return;
      }

      if (this.standard.store == undefined || this.standard.store == "") {
        this.$message({
          type: "error",
          message: "请输入库存",
          showClose: true
        });
        return;
      }

      this.standards[this.index] = this.standard;
      this.dialogFormVisible = false;
    },
    handleClose: function handleClose(standard) {
      this.standards.splice(this.standards.indexOf(standard), 1);
    },

    /*
    文件上传操作
     */
    handleImgSuccess: function handleImgSuccess(res) {
      this.good.imgs = res.data;
      this.save();
    },
    handleChange: function handleChange(file, fileList) {
      this.fileList = fileList.slice(-3);
    },
    handleRemove: function handleRemove() {
      this.fileList.pop();
    },

    /*
    保存数据操作
     */
    //点击提交按钮
    submit: function submit() {
      if (this.good.name == undefined || this.good.name.trim() == "") {
        this.$message({
          type: "error",
          message: "请输入商品名称",
          showClose: true
        });
        return false;
      }

      if (this.good.description == undefined || this.good.description.trim() == "") {
        this.$message({
          type: "error",
          message: "请输入商品描述",
          showClose: true
        });
        return false;
      }

      if (this.good.standards.length === 0) {
        this.$message({
          type: "error",
          message: "请至少添加一个规格",
          showClose: true
        });
        return false;
      }

      if (this.good.discount == undefined || this.good.discount.trim() == "") {
        this.$message({
          type: "error",
          message: "请输入商品折扣",
          showClose: true
        });
        return false;
      }

      if (this.good.categoryId == undefined || this.good.categoryId.trim() == "") {
        this.$message({
          type: "error",
          message: "请选择商品分类",
          showClose: true
        });
        return false;
      }

      if (this.fileList.length === 0) {
        this.$message({
          type: "error",
          message: "请上传图片",
          showClose: true
        });
        return false;
      } // 先上传图片再保存数据


      if (this.fileList[0].status === "ready") {
        this.$refs.upload.submit();
      } else if (this.fileList[0].status === "success") {
        //没有修改初始的图片。直接保存数据
        this.save();
      }
    },
    //保存数据
    save: function save() {
      var _this2 = this;

      API.post(url, this.good).then(function (res2) {
        if (res2.code === "200") {
          _this2.good.id = res2.data;

          _this2.request.post(url + "/standard?goodId=" + _this2.good.id, _this2.standards).then(function (res) {
            if (res.code === "200") {
              _this2.$message({
                type: "success",
                message: "操作成功",
                showClose: true
              });

              _this2.$router.go(-1);
            } else {
              _this2.$message({
                type: "error",
                message: "操作失败",
                showClose: true
              });
            }
          });
        } else {
          _this2.$message({
            type: "error",
            message: res2.msg
          });
        }
      });
    }
  }
};
