import OrderItem from "@/components/OrderItem";
import API from "@/utils/request";
export default {
  name: "OrderList",
  components: {
    'order-item': OrderItem
  },
  data: function data() {
    return {
      orders: {}
    };
  },
  created: function created() {
    var _this = this;

    API.get("/userid").then(function (res) {
      API.get("/api/order/userid/" + res).then(function (res) {
        if (res.code === '200') {
          _this.orders = res.data;

          for (var i = 0; i < _this.orders.length; ++i) {
            console.log(_this.orders);
            _this.orders[i].createTime = _this.orders[i].createTime.toLocaleString().replace(/T/g, ' ').replace(/\.[\d]{3}Z/, '');
          }
        }
      });
    });
  }
};
